import io from "socket.io-client"
const socket = io()

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    header: {
        padding: "1rem 0 2rem 0",
        textAlign: "center",
        // borderBottom: "5px solid #003249",
        marginBottom: "3rem"
