    socket.on("hello", () => {
        loadSix()
    })

    return (
        <Container style={{ paddingTop: "2rem" }}>
            <Grid container>
                <Grid item xs={12} className={classes.header}>
                    <Typography variant="h2"
                    className="heading"
                    //  style={{ color: "#FFD400", fontWeight: "600", borderBottom:"2px solid #FFD400", paddingBottom:"1rem" }}
