        //         <Form.Control
        //             name="time"
        //             placeholder="0000"
        //             // type="number"
        //             onChange={props.onChange}
        //         />
        //     </Form.Group>
        //     <Button
        //         onClick={props.onSubmit}
        //         variant="primary"
        //         type="submit">
        //         Submit
        //     </Button>
        // </Form>
    )
}

export default EtaUpdate
