                label="Oxygen Sat"
                variant="outlined"
                name="oygen"
                onChange={handleVitalsInput} />
            <Button size="large" variant="contained" color="primary" onClick={(event) => handleSubmit(event, vitals)}>Submit</Button>
        </form>
        </Grid>
    );
