function Home() {
    return (
        <div className="container" >
            <div
                style={{ disply: "flex", textAlign: "center", padding: "1rem 0 2rem 0", borderBottom: "5px solid #FFD400", marginBottom: "3rem" }}
            // className="jumbotron" 
            >
                <h1 style={{ color: "#FFD400" }}>Welcome to INREP</h1>
                <h4 style={{ color: "#B7D5D4" }}>A tool to help EMS providers reliably send reports to Hospitals without worrying about radio signal or cell service</h4>
            </div>
            <div>
                <Row style={{ display: "flex", justifyContent: "center", color: "white" }}>
                    <h2>To begin, select what type of user you are:</h2>
                </Row>
                <Row style={{ height: "fit-content" }}>
                    <Col style={{
                        display: "flex", justifyContent: "space-around",
                        // backgroundImage: `url(${ambulance})`, backgroundSize: "cover", backgroundRepeat: "no-repeat"
                    }}>
                        <a
                            style={{ display:"flex", justifyContent:"center", alignItems:"center" ,textAlign:"center", height: "fit-content", width: "100%", backgroundImage: `url(${ambulance})`, backgroundSize:"contain", backgroundRepeat:"no-repeat" }} 
                        href="/ems"><span style={{color:"#FFD400", fontSize: "3rem"}}>EMS Provider</span></a>
                    {/* <Button href="/ems">EMS Provider</Button> */}
                    </Col>
                <Col style={{ display: "flex", justifyContent: "space-around" }}>
                    <a href="/hospital">Hospital</a>
                    {/* <Button href="/hospital">Hosipial</Button> */}
                </Col>
                </Row>
        </div>

        </div >
    )
}

export default Home

