import React, { useEffect } from "react";
import { Card, ListGroup, ListGroupItem, Accordion, Table } from "react-bootstrap"
import Moment from 'react-moment';
import 'moment-timezone';

import VitalsTable from "../Vitals/VitalsTable"

function PatientCard(props) {
    useEffect(() => {
        fetchTrueWarnings(props.patient.criticalWarn)

    }, [])

    let warningKeys = []
    function fetchTrueWarnings(obj) {
        // await loadById(id)
        for (const property in obj) {
            if (obj[property] === true) {
                warningKeys.push(property)
            }
            return warningKeys
        }
        console.log(warningKeys)
    }
    return (
        <Card style={{ marginBottom: "1rem", paddingBottom: "1rem" }}>
            <Card.Header>
                Unit Responding: {props.patient.respondingUnit} |
                ETA: {props.patient.ETA}
            </Card.Header>
            <Card.Body>
                <Card.Title>Critical Warnings: </Card.Title>
                {/* <Card.Text>ETA: {props.patient.ETA}</Card.Text> */}
                <Accordion defaultActiveKey="0">
                    <Card>
                        <Accordion.Toggle eventKey="vitals">Vitals</Accordion.Toggle>
                        <Accordion.Collapse eventKey="vitals">
                            <VitalsTable patient={props.patient} />
                        </Accordion.Collapse>
                    </Card>
                </Accordion>
                <ListGroup className="list-group-flush">
                    <ListGroupItem><b>Last Update: </b>
                        <Moment tz="America/Denver" format="HH:mm:ss MM/DD/YYYY">
                            {props.updatedAt}
                        </Moment>
                    </ListGroupItem>
                </ListGroup>
            </Card.Body>
        </Card>
    )
}

export default PatientCard
