import React from "react"
import { Card, Row, Col, Button } from "react-bootstrap"
import "./style.css"


// const styles = {
//     jumbotron: {
//         backgroundColor: "#007EA7",
//         textAlign: "center",
//         // marginTop: "1rem"
//     },
//     card: {
//         backgroundColor: "#007EA7",
//         textAlign: "center"
//     },
//     cardBody: {
//         display: "flex",
//         justifyContent: "space-around"
//     }
// }

function Home() {
    return (
        <div className="container" >
            <div
                style={{ disply: "flex", textAlign: "center", padding: "1rem 0 2rem 0", }}
            // className="jumbotron" 
            >
                <h1 style={{ color: "#FFD400" }}>Welcome to INREP</h1>
                <h4 style={{ color: "#B7D5D4" }}>A tool to help EMS providers reliably send reports to Hospitals without worrying about radio signal or cell service</h4>
            </div>
            <div>
                <Row style={{ display: "flex", alignItems: "center" }}>
                    <h2>To begin, select what type of user you are:</h2>
                </Row>
                <Row style={{ display: "flex", justifyContent:"space-around" }}>
                    <Col>
                        <Button href="/ems">EMS Provider</Button>
                    </Col>
                    <Col>
                        <Button href="/hospital">Hosipial</Button>
                    </Col>
                </Row>
            </div>
            <Card >
                <Card.Header><h2>To begin, select what type of user you are:</h2></Card.Header>
                <Card.Body >
                    <Card.Link
                        className="btn btn-dark"
                        href="/ems"
                    >
                        <h4>EMS Provider</h4>
                    </Card.Link>
                    <Card.Link
                        className="btn btn-dark"
                        href="/hospital"
                    >
                        <h4>Hospital</h4>
                    </Card.Link>
                    {/* <a
                        className="btn btn-dark"
                        href="/ems"
                    ><h4>EMS Provider</h4></a>
                    <a
                        className="btn btn-dark"
                        href="/hospital"
                    ><h4>Hospital</h4>
                    </a> */}
                </Card.Body>
            </Card>
        </div>
    )
}

export default Home
