import React, { useState } from "react"
// import { Form, Button } from "react-bootstrap"

import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid'
import Button from '@material-ui/core/Button'

const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            margin: theme.spacing(2),
            width: '25ch',
        },
    },
    formgroup: {
        display: "inline"
    },
    input: {
        marginBottom: "0.3rem",
        backgroundColor:"white",
        borderRadius: "4px"
    }
}));

function PatientInformationForm(props) {
    const classes = useStyles()

    const [patientInformation, setPatientInformation] = useState({
        age: "Not Reported",
        sex: "Not Reported",
        chiefComplaint: "Not Reported",
        moiHpi: "Not Reported",
        oxygenTx: "Not Reported",
        from: "Not Reported"
    })

    function handlePtInformationChange(event) {
        const { name, value } = event.target
        setPatientInformation({ ...patientInformation, [name]: value })
    }

    function handleSubmit(event, data) {
        // console.log(data)
        props.onSubmit(event, data)
    }

    return (
        <form className={classes.root} noValidate autoComplete="off">
            <Grid container className={classes.formgroup}>
                <Grid item xs={12}>
                    <TextField
                        className={classes.input}
                        id="outlined-basic"
                        label="Age"
                        variant="outlined"
                        name="age"
                        onChange={handlePtInformationChange} />
                    <TextField
                        className={classes.input}
                        id="outlined-basic"
                        label="Sex"
                        variant="outlined"
                        name="sex"
                        onChange={handlePtInformationChange} />
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        fullWidth
                        className={classes.input}
                        id="outlined-basic"
                        label="Chief Complaint"
                        variant="outlined"
                        name="chiefComplaint"
                        onChange={handlePtInformationChange} />
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        fullWidth
                        className={classes.input}
                        id="outlined-basic"
                        label="MOI/ HPI"
                        variant="outlined"
                        name="moiHpi"
                        onChange={handlePtInformationChange} />
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        className={classes.input}
                        id="outlined-basic"
                        label="O2 Treatment"
                        variant="outlined"
                        name="oxygenTx"
                        onChange={handlePtInformationChange} />
                    <TextField
                        className={classes.input}
                        id="outlined-basic"
                        label="Coming From"
                        variant="outlined"
                        name="from"
                        onChange={handlePtInformationChange} />
                </Grid>
                <Button size="large" variant="contained" color="primary" onClick={(event) => handleSubmit(event, patientInformation)}>Submit</Button>
            </Grid>
        </form >
    )

