import React, {useState} from "react"
import {Card, Button} from "react-bootstrap"

function CriticalWarnings(props){
    const [editStatus, setEditStatus] = useState(false)
    const pending = editStatus
    let criticals
    {pending ? criticals=<p>pending changes</p>:criticals=""}
    return(
<Card>
                    <Card.Header>Critical Warnings <Button
                         onClick={setEditStatus(true)} 
                        className="btn btn-danger">Update</Button ></Card.Header>
                    <Card.Body>
                        {criticals}
                    </Card.Body>
                </Card>
    )
}

export default CriticalWarnings
