import React, { useEffect, useState } from "react"
import { Component } from "react"
import { useParams } from "react-router-dom"
import API from "../../utils/API"

import { Card, Button } from "react-bootstrap"

function CriticalWarnings(props) {
    const [warnings, setWarnings] = useState({})
    const { id } = useParams()
    useEffect(() => {
        loadById(id)
    },[])
    function loadById(id) {
        // console.log(id)
        API.findById(id)
            .then((ptData) => {
                // console.log(ptData.data.criticalWarn)
                const warningObject = JSON.parse(ptData.data.criticalWarn)
                // console.log(warningObject)
                setWarnings(warningObject)
            })
    }

    function fetchTrueWarnings(obj) {
        let warningKeys = []
        for (const property in obj) {
            if (property.value === "true"){
                warningKeys.push(property.key)
            }
        }
        console.log(warningKeys)
    }
    fetchTrueWarnings(warnings)


    return (
        <Card>
            <Card.Header>Critical Warnings <Button
                onClick={props.onClick}
                className="btn btn-danger">Update</Button ></Card.Header>
            <Card.Body>
            </Card.Body>
        </Card>
    )
}

export default CriticalWarnings
